#include<iostream>
using namespace std;
int main()
{
	int N, M;
	cin >> N >> M;
	int a[60000];
	int ans = 0;
	int  s = 0;
	for (int i = 0; i < N; i++) {
		cin >> a[i];
	}
	for (int i = 0; i < N; i++) {
		if (i == 0) {
			if (M > a[i]) {
				ans++;
				M = M - a[i];
			}
			else {
				break;
			}
		}
		else {
			if (M > a[i] * (i + 1)) {
				ans += (i + 1);
				M -= a[i] * i;
			}
			else {
				do {
					if (M > a[i]) {
						ans++;
						M -= a[i];
					}
					else {
						break;
					}
					s++;
				} while (s == i - 1);
			}
		}
	}
	cout << ans;
}
