#include <iostream>
#include <utility>
#include <vector>

struct Data {
    int s, t;
    int v;
};
int cnt;
int main() {
    int n, m;
    std::cin >> n >> m;

    std::vector<Data> setQ;
    setQ.reserve(m);

    for (int i = 0; i < m; ++i) {
        int q;
        std::cin >> q;

        if (q == 1) {
            int s, t, v;
            std::cin >> s >> t >> v;
            setQ.push_back({s, t, v});
            cnt++;
        }
        else {
            int k;
            std::cin >> k;

            for (int j = cnt - 1; j >= 0; --j) {
                if (setQ[j].s <= k && k < setQ[j].t) {
                    std::cout << setQ[j].v << std::endl;
                    break;
                }

                if (j == 0)
                    std::cout << 0 << std::endl;
            }
        }
    }
}
