using namespace std;
int main(void) {
	const string tanc = "tancahn";
	vector<char> strArray, tanArray;
	string str;
	cin >> str;
	if (tanc.size() != str.size()) {
		Yes(false);
		return 0;
	}
	rep(i, tanc.size()) {
		tanArray.push_back(tanc[i]);
	}
	sort(tanArray.begin(), tanArray.end());

	rep(i, str.size()) {
		strArray.push_back(str[i]);
	}
	sort(strArray.begin(), strArray.end());

	Yes(strArray == tanArray);
