#include <iostream>
#include <utility>
#include <vector>
using LL = long long;
LL cnt, a[2000000][3];
int main() {
	LL n, m;
	std::cin >> n >> m;

	for (LL i = 0; i < m; ++i) {
		int q;
		std::cin >> q;

		if (q == 1) {
			LL s, t, v;
			std::cin >> s >> t >> v;
			a[cnt][0] = s, a[cnt][1] = 1, a[cnt][2] = v;
			cnt++;
		}
		else {
			LL k;
			std::cin >> k;
			int ans = 0;
			for (LL j = cnt - 1; j >= 0; --j) {
				if (a[j][0] <= k && k < a[j][1]) {
					ans = a[j][2];
					break;
				}
			}
			std::cout << ans << std::endl;
		}
	}
	return 0;
}
