bool compare_sa(const int &i, const int &j)
{
	if (order[i] != order[j]) return order[i] < order[j];
	else
	{
		int ri = i + k <= n ? order[i + k] : -1;
		int rj = j + k <= n ? order[j + k] : -1;
		return ri < rj;
	}
}

void construct_sa()
{
	for (int i = 0; i < n; i++)
	{
		sa[i] = i;
		order[i] = i < n ? str[i] : -1;
	}
	for (k = 1; k < n; k *= 2)
	{
		sort(sa, sa+n, compare_sa);
		tmp[sa[0]] = 0;
		for (int i = 1; i < n; i++)
			tmp[sa[i]] = tmp[sa[i - 1]] + (compare_sa(sa[i - 1], sa[i]) ? 1 : 0);
		for (int i = 0; i < n; i++)
			order[i] = tmp[i];
	}
}
