# include <iostream>
# include <algorithm>
# include <vector>
# include <string>
# include <map>
#define ll long long
#define rep(i,rept) for (ll i = 0; i < rept; i++)
#define Yes(b) std::cout<< (b ? "Yes" : "No")<<std::endl
using namespace std;
int main(void) {
	map<char, int> m = {}, charMap = {};
	int n;
	string str;
	vector<char> firstChars = {}, lastChars = {},charType;
	cin >> n;
	rep(i, n) {
		cin >> str;
		firstChars.push_back(str[0]);
		lastChars.push_back(str[str.size() - 1]);
		if (charMap[str[0]] == 0) {
			charType.push_back(str[0]);
		}
		if (str[0] != str[str.size() - 1]) {
			++m[str[0]];
		}
		charMap[str[0]]++;
	}
	sort(firstChars.begin(), firstChars.end());
	sort(lastChars.begin(), lastChars.end());

	if (firstChars == lastChars) {
		if (charMap.size() == 1) {
			cout << "Yes" << std::endl;
			return 0;
		}
		rep(i, charType.size() - 1) {
			if (m[charType[i]] == 0) {
				cout << "No" << std::endl;
				return 0;
			}
		}
		cout << "Yes" << std::endl;
	}
	else { cout << "No" << endl; };
	return 0;
}
