#include<iostream>
int main() {
	int H, W;
	std::cin >> H >> W;
	for (int i = 0; i<H; i++) {
		char a, b;
		std::cin >> a;
		if (a == '.') {
			std::cout << -1 << ' ';
			b = -1;
			}
			else {
				std::cout << 0 << ' ';
				b = 0;
			}
			for (int j = 0; j<W - 2; j++) {
				std::cin >> a;
				if (a == 'c') {
					std::cout << 0 << ' ';
					b = 0;
				}
				else if (b >= 0) {
					std::cout << b + 1 << ' ';
					b += 1;
				}
				else {
					std::cout << -1 << ' ';
				}
			}
			std::cin >> a;
			if (a == 'c') {
				std::cout << 0 << std::endl;
			}
			else if (b >= 0) {
				std::cout << b + 1 << std::endl;
			}
			else {
				std::cout << -1 << std::endl;
			}

		}
	}
