using ll = long long;
using namespace std;
typedef unsigned long long ull;
typedef pair<ll, ll> P;
 
const ll MOD = 1000000007;
const ll INF = 1 << 30;
const ll INF2 = 9000000000000000000LL;
const double INF3 = 900000000000000;
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };
const int tx[8] = { -1,0,1,-1,1,-1,0,1 }, ty[8] = { -1,-1,-1,0,0,1,1,1 };
#define ALL(x) (x).begin(),(x).end()
 
ll mod_pow(ll x, ll n) {
	ll res = 1;
	while (n > 0) {
		if (n & 1) res = res*x%MOD;
		x = x*x%MOD;
		n >>= 1;
	}
	return res;
}
 
int main() {
		ll n, w;
		cin >> n >> w;
		if (w >= -9 && w < 9) {
			if (w < 0) cout << mod_pow(10, n - 2)*(9 - abs(w) + 1) % MOD << endl;
			else cout << mod_pow(10, n - 2)*(9 - w) % MOD << endl;
		}
		else cout << 0 << endl;
	return 0;
}
