#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<ll, ll> P;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll INF = 9e18;
const double DINF = 5e14;
const double eps = 1e-10;
const int di[4] = { 1,0,-1,0 }, dj[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

struct edge { ll to, cost; };
vector<edge>v[10010];
ll D[10010];

void dijkstra(int n) {
	for (int i = 0;i <= n;i++) {
		D[i] = INF;
	}
	D[1] = 1;
	priority_queue<P, vector<P>, greater<P>>pq;
	pq.push(P(D[1], 1));
	while (!pq.empty()) {
		P p = pq.top();
		pq.pop();
		if (D[p.sc] < p.fr)continue;
		for (auto e: v[p.sc]) {
			if (D[e.to] <= D[p.sc] + e.cost)continue;
			D[e.to] = D[p.sc] + e.cost;
			pq.push(P(D[e.to], e.to));
		}
	}
	return;
}

int K, d;
string p;
int m;
int dp[72][2][10010][37];//i=桁,j=AorB未満か,k=MOD M,l=digit sum

int main() {
	cin >> K >> p;
	for (int i = 0;i < K;i++) {
		v[i].pb(edge{ (i + 1) % K, 1 });
	}
	for (int i = 1;i < K;i++) {
		if ((i * 10) % K != i)v[i].pb(edge{ (i * 10) % K,0 });
	}
	dijkstra(K);
	m = D[0];
	dp[0][1][0][0] = 1;
	for (int i = 0;i < p.size();i++) {
		for (int j = 0;j < 10;j++) {
			for (int k = 0;k < K;k++) {
				for (int n = 0;n <= m;n++) {
					if (n + j > m)continue;

					if (p[i] - '0' > j) {
						dp[i + 1][0][(k * 10 + j) % K][n + j] += dp[i][0][k][n];
						dp[i + 1][0][(k * 10 + j) % K][n + j] += dp[i][1][k][n];
					}
					else if (p[i] - '0' == j) {
						dp[i + 1][0][(k * 10 + j) % K][n + j] += dp[i][0][k][n];
						dp[i + 1][1][(k * 10 + j) % K][n + j] += dp[i][1][k][n];
					}
					else {
						dp[i + 1][0][(k * 10 + j) % K][n + j] += dp[i][0][k][n];
					}
					dp[i + 1][0][(k * 10 + j) % K][n + j] %= MOD;
					dp[i + 1][1][(k * 10 + j) % K][n + j] %= MOD;
				}
			}
		}
	}
	cout << dp[p.size()][0][0][m]% MOD << endl;
	return 0;
}
