#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<int, int>pi;
typedef pair<ll, ll> P;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll INF = 9e18;
const double DINF = 5e14;
const double eps = 1e-10;
const int di[4] = { 1,0,-1,0 }, dj[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll n, b[100010], c[100010], x[100010], ans[300010];
int a[100010], q;
vector<ll>v;

int main() {
	cin >> n >> q;
	for (int i = 0;i < q;i++) {
		cin >> a[i];
		if (a[i]) {
			cin >> b[i];
			v.eb(b[i]);
		}
		else {
			cin >> b[i] >> c[i] >> x[i];
			v.eb(b[i]);
			v.eb(c[i]);
		}
	}
	sort(ALL(v));
	v.erase(unique(ALL(v)), v.end());
	for (int i = 0;i < q;i++) {
		if (a[i])continue;
		int it = lower_bound(ALL(v), b[i]) - v.begin();
		int it2 = upper_bound(ALL(v), c[i]) - v.begin();
		ans[it] += x[i];
		ans[it2] -= x[i];
	}
	for (int i = 0;i < 300001;i++) {
		ans[i + 1] += ans[i];
	}
	for (int i = 0;i < q;i++) {
		if (!a[i])continue;
		cout << ans[lower_bound(ALL(v), b[i]) - v.begin()] << endl;
	}
	return 0;
}
