#include <iostream>
#include <algorithm>
int sa(int n, int m) {
	if (n >= 0) {
		if (m >= 0)
			return max(n, m) - min(n, m);
		else
			return n + abs(m);
	}
	else {
		if (m >= 0)
			return m - n;
		else
			return abs(min(n, m)) - abs(max(n, m));
	}
}
int main() {
	ll a, b, c, d, n, m, o, p;
	cin >> a >> b >> c >> d >> n >> m >> o >> p;

	if (sa(b, d) / sa(a, c) != sa(m, p) / sa(n, o))
		cout << "Intersect" << endl;
	else
		cout << "Parallel" << endl;
	return 0;
}
