#include<iostream>
#include<vector>
#include<algorithm>
using namespace std;
int main(){
    int l,k,n;
    int sum=0;
    int goukei=0;
    vector<int>a(n);
    cin>>n>>l>>k;
    for(int i=0;i<n;i++){
        cin>>a[i];
    }
    for(int i=0;i<n;i++){
        if(a[i]>=l){
            sum+=k;
        }else{
            goukei++;
        }
    }
    cout<<sum<<endl;
    cout<<goukei<<endl;
}
