#include <functional>
#define rep(i,n) for(int i=0;i<n;++i)

using namespace std;
int n, l, k, A[101010], ans, add;
int main() {
	cin >> n >> l >> k;
	rep(i, n) {
		cin >> A[i];
		if (A[i] > l) ans += k;
		else add++;
	}
	cout << ans << endl << add << endl;
	return 0;
}
