#include<iostream>
int main() {
	int n, l, k,sum[2];
	std::cin >> n >> l >> k;
	sum[0] = 0, sum[1] = 0;
	for (int i = 0; i < n; i++) {
		int x;
		std::cin >> x;
		if (x >= l) {
			sum[0] += k;
		}
		else {
			sum[1] += 1;
		}
	}
	std::cout << sum[0] << std::endl;
	std::cout << sum[1] << std::endl;
}
