#include<iostream>
int main()
{
	int A[2], B[2], C[2], D[2], AB[2], CD[2];
	for (int i = 0; i <= 1; i++) {
		std::cin >> A[i];
	}
	for (int i = 0; i <= 1; i++) {
		std::cin >> B[i];
	}
	for (int i = 0; i <= 1; i++) {
		std::cin >> C[i];
	}
	for (int i = 0; i <= 1; i++) {
		std::cin >> D[i];
	}
	for (int i = 0; i <= 1; i++) {
		AB[i] = A[i] - B[i];
		CD[i] = C[i] - D[i];
		if (AB[i] < 0) {
			AB[i] = B[i] - A[i];
		}
		if (CD[i] < 0) {
			CD[i] = D[i] - C[i];
		}
	}
	if (AB[0] == CD[0]) {
		if (AB[1] == CD[1]) {
			std::cout << "Parallel" << std::endl;
		}
		else if (AB[1] % CD[1] == 0) {
			std::cout << "Parallel" << std::endl;
		}
		else if (CD[1] % AB[1] == 0) {
			std::cout << "Parallel" << std::endl;
		}
		else {
			std::cout << "Intersect" << std::endl;
		}
	}
	else if (AB[0] % CD[0] == 0) {
		if (AB[1] == CD[1]) {
			std::cout << "Parallel" << std::endl;
		}
		else if (AB[1] % CD[1] == 0) {
			std::cout << "Parallel" << std::endl;
		}
		else if (CD[1] % AB[1] == 0) {
			std::cout << "Parallel" << std::endl;
		}
		else {
			std::cout << "Intersect" << std::endl;
		}
	}
	else if (CD[0] % AB[0] == 0) {
		if (AB[1] == CD[1]) {
			std::cout << "Parallel" << std::endl;
		}
		else if (AB[1] % CD[1] == 0) {
			std::cout << "Parallel" << std::endl;
		}
		else if (CD[1] % AB[1] == 0) {
			std::cout << "Parallel" << std::endl;
		}
		else {
			std::cout << "Intersect" << std::endl;
		}
	}
}
