#include <iostream>

int main(){

	long long N, L, K;

	std::cin >> N >> L >> K;

	long long ans = 0;
	long long unknown = 0;

	for(int i = 0; i < N; ++i){

		long long l;

		std::cin >> l;

		if(l >= L){
			ans += K;
		}
		else{
			++unknown;
		}
	}

	std::cout << ans << std::endl;
	std::cout << unknown << std::endl;

	return 0;
}
