#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<int, int>pi;
typedef pair<ll, ll> P;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll INF = 9e18;
const double DINF = 5e14;
const double eps = 1e-10;
const int di[4] = { 1,0,-1,0 }, dj[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

int gcd(int a, int b) {
	if (b == 0)return a;
	return gcd(b, a%b);
}

int n, a[110], ans;
bool dp[101][100010];

int main() {
	cin >> n;
	for (int i = 0;i < n;i++) {
		cin >> a[i];
	}
	dp[0][0] =1;
	for (int i = 0;i < n;i++) {
		for (int j = 0;j <= 100000;j++) {
			if (!dp[i][j])continue;
			dp[i + 1][j] = 1;
			if (gcd(abs(j - a[i]), a[i]) != 1) {
				dp[i + 1][j + a[i]] = 1;
			}
		}
	}
	for (int i = 0;i <=100000;i++) {
		if (dp[n][i])ans = max(ans, i);
	}
	cout << ans << endl;
	return 0;
}
