#include<iostream>
#include<vector>
#include<algorithm>
using namespace std;
int main(){
    unsigned long long sum=0,n,goukei=0,l,k;
    vector<unsigned long long int>a(n);
    cin>>n>>l>>k;
    for(int i=0;i<n;i++){
        cin>>a[i];
    }
    for(int i=0;i<n;i++){
        if(a[i]>=l){
            sum+=k;
        }else{
            goukei++;
        }
    }
    cout<<sum<<endl;
    cout<<goukei<<endl;
}
