ll gcd(ll a, ll b) {
	if (b%a == 0)return a;
	return gcd(b%a,a);
}
vector<ll>mod;
ll n, v[110],ans;
int main() {
	cin >> n;
	rep(i, n)cin >> v[i];
	mod.pb(2);
	for (int i = 3; i < 1000; i += 2)mod.pb(i);
	rep(i, mod.size()) {
		ll sum=0;
		rep(j,n) {
			if (v[j] % mod[i] == 0)sum += v[j];
		}
		ans = max(ans, sum);
	}
	cout << ans << endl;
	return 0;
}
