#include <iostream>
#include <string>

constexpr int MAX_M = 1000000000;

int main(){

	std::string N;
	int M;

	std::cin >> N >> M;

	int ans = 0;

	if(N.length() > 10){
		ans += MAX_M % M;
		ans += std::atoi(N.substr(N.length() - 11, 10).c_str());
	}
	else{
		ans += std::atoi(N.c_str());
	}

	ans %= M;

	std::cout << ans << std::endl;

	return 0;
}
