#include<iostream>
int main()
{
	int N, L, K;
	int l[100000];
	int ans1 = 0, ans2 = 0;
	std::cin >> N >> L >> K;
	for (int i = 0; i < N; i++) {
		std::cin >> l[i];
		if (l[i] > L) {
			ans1 += K;
		}
		else {
			ans2 += 1;
		}
	}
	std::cout << ans1 << std::endl << ans2 << std::endl;
}
