using namespace std;
int main() {
	int m, count = 1, a[100005] = {};
	
	ll sum = 0;
	string s;
	cin >> s >> m;
	a[0] = 0;
	for (int i = s.size() - 1; i >= 0;--i) {
		
		if (count == 1) {
			sum += ((std::stoi(string{ s[i] }) % m));
			a[count] = 1;
		}
		else {
			a[count] = ((a[count - 1]) * 10) % m;
			sum += ((std::stoi(string{ s[i] }) % m)*(a[count])) % m;
		}
		sum %= m;
		count++;
		
	}
	cout << sum;
}
