#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<int, int>pi;
typedef pair<ll, ll> P;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll INF = 9e18;
const double DINF = 5e14;
const double eps = 1e-10;
const int di[4] = { 1,0,-1,0 }, dj[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

struct edge { ll to, cost; };

int n, m, f, t, c;
ll d[1010][5][13], ans=INF;
vector<edge>v[1010];

void dijk() {
	for (int i = 0;i < n;i++) {
		for (int j = 0;j < 5;j++) {
			for (int k = 0;k < 13;k++) {
				d[i][j][k] = INF;
			}
		}
	}
	d[0][0][0] = 0;
	priority_queue<PPP, vector<PPP>, greater<PPP>>pq;
	pq.push(PPP(P(0, 0), P(0, 0)));
	while (!pq.empty()) {
		PPP p = pq.top();
		pq.pop();
		ll co = p.fr.fr, now = p.fr.sc, mod5 = p.sc.fr, mod13 = p.sc.sc;
		if (d[now][mod5][mod13] < co)continue;
		for (edge u : v[now]) {
			if (d[u.to][(mod5 + u.cost) % 5][(mod13 + u.cost) % 13] <= d[now][mod5][mod13] + u.cost)continue;
			d[u.to][(mod5 + u.cost) % 5][(mod13 + u.cost) % 13] = d[now][mod5][mod13] + u.cost;
			if (u.to == n - 1)continue;
			pq.push(PPP(P(d[u.to][(mod5 + u.cost) % 5][(mod13 + u.cost) % 13], u.to), P((mod5 + u.cost) % 5, (mod13 + u.cost) % 13)));
		}
	}
}

int main() {
	cin >> n >> m;
	for (int i = 0;i < m;i++) {
		cin >> f >> t >> c;
		v[f].eb(edge{ t,c });
		v[t].pb(edge{ f,c });
	}
	dijk();
	for (int i = 0;i < 5;i++) {
		for (int j = 0;j < 13;j++) {
			if (i == 0 || j == 0) {
				ans = min(ans, d[n - 1][i][j]);
			}
		}
	}
	cout << ans << endl;
	return 0;
}
