void dijk(LL s) {
	rep(i, 1100)
		rep(j, 65)
		d[i][j] = INF;
	d[s][0] = 0;
	priority_queue<Ppll, vector<Ppll>, greater<Ppll>> pq;
	pq.push(Ppll(d[s][0], Pll(s, 0))); // (cost,頂点番号) 
	while (!pq.empty()) {
		Ppll pp;
		pp = pq.top();
		LL cos = pp.first, vv = pp.sec.fir,md=pp.sec.sec;
		pq.pop();
		if (d[vv][md] < pp.first)continue;
		for (int i = 0; i < vec[vv].size(); i++) {
			if (vec[vv][i].cost + d[vv][md] < d[vec[vv][i].to][(md + vec[vv][i].cost) % 65]) {
				d[vec[vv][i].to][(md + vec[vv][i].cost) % 65] = vec[vv][i].cost + d[vv][md];
				pq.push(Ppll(d[vec[vv][i].to][(md + vec[vv][i].cost) % 65], Pll(vec[vv][i].to, (md + vec[vv][i].cost) % 65)));
			}
		}
	}
}

