#include <iostream>

int main() {

	int N, L, K, l, X, Y;
	
	std::cin >> N >> L >> K;

	for (int i = 0; i < N;++i) {
		

		std::cin >> l;

		X = 0;

		Y = 0;

		if (l < L) {

			for (int i = 0; i < N;++i) {


				Y += 1;

				X += 0;
			}
		}
		else {

			for (int i = 0; i < N;++i) {


				X += K;

				Y += 0;
			}
		}
	}
	std::cout << X << std::endl;
	std::cout << Y << std::endl;
	return 0;
}
