typedef long long ll;
using namespace std;

ll nCr(ll n, ll r) {
	if (r > n) { return 0; }
	if (r * 2 > n) r = n - r;
	ll dividend = 1;
	ll divisor = 1;
	for (ll i = 1; i <= r; ++i) {
		dividend *= (n - i + 1);
		divisor *= i;
	}
	return dividend / divisor;
}
ll sumdigit(ll n) {
	ll dig = 0, sum = 0;
	while (n) {
		dig = n % 10;
		sum = sum + dig;
		n = n / 10;
	}
	return sum;
}


int main() {
	std::string n;
	std::string str;
	ll m = 0,i = 0;
	ll ans = 0;
	std::cin >> n >> m;
	while (i < n.length()) {
		str = n.substr(i, m);
		if (str[0] != '0') {
			ans += std::stoi(str) % m;
		}
		i = i + m;
	}
	std::cout << ans % m << std::endl;

}
