#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<ll, ll> P;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll INF = 9e18;
const double DINF = 5e14;
const double eps = 1e-10;
const int di[4] = { 1,0,-1,0 }, dj[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

struct edge { ll to, cost; };
vector<edge>v[10010];
ll D[10010];

void dijkstra(int n) {
	for (int i = 0;i <= n;i++) {
		D[i] = INF;
	}
	D[1] = 1;
	priority_queue<P, vector<P>, greater<P>>pq;
	pq.push(P(D[1], 1));
	while (!pq.empty()) {
		P p = pq.top();
		pq.pop();
		if (D[p.sc] < p.fr)continue;
		for (auto e : v[p.sc]) {
			if (D[e.to] <= D[p.sc] + e.cost)continue;
			D[e.to] = D[p.sc] + e.cost;
			pq.push(P(D[e.to], e.to));
		}
	}
	return;
}

int K;
string p;
int m;
int dp[2][2][10001][37];

int main() {
	cin >> K >> p;
	for (int i = 0;i < K;i++) {
		v[i].eb(edge{ (i + 1) % K, 1 });
	}
	for (int i = 1;i < K;i++) {
		if ((i * 10) % K != i)v[i].eb(edge{ (i * 10) % K,0 });
	}
	dijkstra(K);
	m = D[0];
	dp[0][1][0][0] = 1;
	for (int i = 0;i < p.size();i++) {
		for (int j = 0;j < 10;j++) {
			for (int k = 0;k < K;k++) {
				for (int n = 0;n <= m;n++) {
					if (n + j > m)continue;
					if (p[i] - '0' > j) {
						dp[1][0][(k * 10 + j) % K][n + j] += dp[0][0][k][n];
						dp[1][0][(k * 10 + j) % K][n + j] += dp[0][1][k][n];
					}
					else if (p[i] - '0' == j) {
						dp[1][0][(k * 10 + j) % K][n + j] += dp[0][0][k][n];
						dp[1][1][(k * 10 + j) % K][n + j] += dp[0][1][k][n];
					}
					else {
						dp[1][0][(k * 10 + j) % K][n + j] += dp[0][0][k][n];
					}
					dp[1][0][(k * 10 + j) % K][n + j] %= MOD;
					dp[1][1][(k * 10 + j) % K][n + j] %= MOD;
				}
			}
		}
		for (int k = 0;k < K;k++) {
			for (int n = 0;n <= m;n++) {
				dp[0][0][k][n] = dp[1][0][k][n];
				dp[0][1][k][n] = dp[1][1][k][n];
				dp[1][0][k][n] = 0, dp[1][1][k][n] = 0;
			}
		}
	}
	cout << dp[0][0][0][m] % MOD << endl;
	return 0;
}
