# pragma warning(disable:4996)

struct edge { pair<LL,LL> to; LL cost; };
int start = 0;
LL d[1010][101]; //距離
vector<edge> vec[1010][101];


int n, m;

typedef pair<LL, pair<LL,LL>> pii;

void dijkstra() {
	for (int i = 0; i < 1010; i++)for (int j = 0; j < 65; j++)d[i][j] = HINF<LL>();
	priority_queue<pii, std::vector<pii>, std::greater<pii>> pq;
	d[start][0] = 0;
	pq.push(make_pair(d[start][0], make_pair(start, 0)));//cost,to
	while (!pq.empty()) {
		pii p = pq.top();
		pq.pop();
		int i = p.second.first;
		int j = p.second.second;
		if (d[i][j] < p.first)continue;
		for (int k = 0; k < vec[i][j].size(); k++) {
			edge e = vec[i][j][k];
			if (d[e.to.first][e.to.second] <= d[i][j] + e.cost)continue;
			d[e.to.first][e.to.second] = d[i][j] + e.cost;
			if (e.to.first == n - 1)continue;
			pq.push(make_pair(d[e.to.first][e.to.second], e.to));
		}
	}
}

int main() {
	cin >> n >> m;
	REP(i, m) {
