		REP(j, 65) {
			vec[f][j].emplace_back(edge{ make_pair(t,(j + c) % 65),c });
			vec[t][j].emplace_back(edge{ make_pair(f,(j + c) % 65),c });
		}
	}
	dijkstra();
	LL ans = HINF<LL>();
	REP(i, 65) {
		if (!(i % 5 == 0 || i % 13 == 0))continue;
		ans = min(ans, d[n - 1][i]);
	}
	cout << ans << endl;
}
