#include <iostream>
#include <algorithm>

int main () {

int n,l,k;
int f[100001];
int cantcount = 0;
int temp = 0;

std::cin >> n >> l >> k;

for (int i = 0; i < n; ++i){
std::cin >> f[i];
}

for (int i = 0; i < n; ++i){
if (f[i] < l){
cantcount += 1;
}
else {
temp += k;
}
}

std::cout << temp << std::endl;
std::cout << cantcount << std::endl;

return 0;


}
