int dp[80][2][37][10000];
int func(string x) {
	rep(i, x.size() + 1)rep(j, 2)rep(n, 37)rep(k, 10000) {
		dp[i][j][n][k] = 0;
	}
	dp[0][0][0][0] = 1;
	rep(i, x.size()) {
		rep(j, 2) {
			rep(l, 36) {
				rep(m, h) {
					int lim = (j ? 9 : x[i] - '0');
					rep(k, lim + 1) {
						if (l + k <= 36) {
							dp[i + 1][j || k < lim][l + k][(m * 10 + k) % h] += dp[i][j][l][m];
							dp[i + 1][j || k < lim][l + k][(m * 10 + k) % h] %= MOD;
						}
					}
				}
			}
		}
	}
	ans = 0;
	rep(j,2)rep(l, 36)  {
		
		if (dp[x.size()][j][l+1][0] > 0) {
			rep(k, 2) {
				ans += dp[x.size()][k][l+1][0];
				ans %= MOD;
			}
			return ans;
		}

	}
	return ans;
}
