#include<iostream>

int main() {
	int N, L, K;
	std::cin >> N >> L >> K;
	int X,Y;
	int l;
	std::cin >> l;
	for (int i = 0; i < N;++i) {
		X = 0;
	if (l < L) {
			X += 0;
		}
		else {
			X += K;
		}
	}
	for (int i = 0; i < N; ++i) {
		Y = 0;
		if (l < L) {
			Y += 1;
		}
		else {
			Y += 0;
		}
	}
	std::cout << X << std::endl;
	std::cout << Y << std::endl;
	return 0;
}
