#include <iostream>

int calc_x(int L, int K, int l) {
	int X;
	if (l < L) {
		X = 0;
	}
	else {
		X = K;
	}
	return X;
	}


int calc_y(int L, int K, int l) {
	int Y;
	if (l < L) {
		Y = 1;
	}
	else {
		Y = 0;
	}
	return Y;
}
int main() {
	int N, L, K, X = 0, Y = 0;
	std::cin >> N >> L >> K;
	for (int i = 0;i < N;++i) {
		int l;
		std::cin >> l;
		X += calc_x(L, K, l);
		Y += calc_y(L, K, l);
	}
	std::cout << X << std::endl;
	std::cout << Y << std::endl;
	return 0;
}
