#include<iostream>
int calc_X(int L, int K, int l) {
	int X;
	if (l < L)
		X = 0;
	else {
		X = K;
	}
	return X;
}
int calc_Y(int L, int K, int l) {
	int Y;
	if (l < L)
		Y = 1;
	else {
		Y = 0;
	}
	return Y;
}
int main() {
	int N, L, K;
	std::cin >> N;
	std::cin >> L;
	std::cin >> K;
	unsigned long int  X = 0, Y = 0;

	for (int i = 0; i < N; ++i) {
		int l;
		std::cin >> l;
		X += calc_X(L, K, l);
		Y += calc_Y(L, K, l);
	}

	std::cout << X << std::endl;
	std::cout << Y << std::endl;


}
