#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<int, int>pi;
typedef pair<ll, ll> P;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll INF = 9e18;
const double DINF = 5e14;
const double eps = 1e-10;
const int di[4] = { 1,0,-1,0 }, dj[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll n, k, l, r;

int main() {
	cin >> n >> k;
	ll l = 0, r = n + 1, mid;
	while (r - l > 1) {
		mid = (r + l) / 2;
		if (k - mid > (k + 1) / 2)l = mid;
		else r = mid;
	}
	ll L = 0, R = n + 1;
	while (R - L > 1) {
		mid = (R + L) / 2;
		if (k - mid <= n)R = mid;
		else L = mid;
	}
	if (r == n + 1 || R == n + 1 || k - R <= 0 || k - R > n || k - r <= 0 || k - r > n)cout << 0 << endl;
	else {
		if (k % 2 == 0 && r == k / 2)r--;
		if (k % 2 == 0 && R == k / 2)R--;
		if (r == n + 1 || R == n + 1 || k - R <= 0 || k - R > n || k - r <= 0 || k - r > n || R <= 0 || r <= 0)cout << 0 << endl;
		else cout << max(0LL, r - R + 1) << endl;
	}
	return 0;
	return 0;
}
