#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<int, int>pi;
typedef pair<ll, ll> P;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll INF = 9e18;
const double DINF = 5e14;
const double eps = 1e-10;
const int di[4] = { 1,0,-1,0 }, dj[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

int n, m, cnt;
int a[1010];

int main() {
	cin >> n >> m;
	for (int i = 1;i <= n;i++) {
		a[i] = 1;
	}
	cnt = n - 1;
	a[1] = 0;
	int now = 1;
	while (true) {
		if (cnt == 1)break;
		int c = 0;
		for (int i = now;;i++) {
			if (!a[i%(n+1)])continue;
			c++;
			if (c == m) {
				a[i%(n+1)]--;
				now = i % (n + 1);
				cnt--;
				break;
			}
		}
	}
	for (int i = 1;i <= n;i++) {
		if (a[i]) {
			cout << i << endl;
			return 0;
		}
	}
	return 0;
}
