#include <iostream>

constexpr int MAX_NM = 1000;

int main(){

	int N, M;

	bool flag[MAX_NM] = {};

	std::cin >> N >> M;

	int i = 0;
	int count = 0;
	while(count < N - 1){
		flag[i % N] = true;
		i += M;
		++count;
	}

	for(int i = 0; i < N; ++i){
		if(!flag[i]){
			std::cout << i + 1 << std::endl;
		}
	}

	return 0;
}
