ll n, q;
map<string, ll>m;
vector<pps>v;
int main() {
	cin >> n;
	ll a, b, c;
	char ch, chh;
	string ss;
	rep(i, n) {
		cin >> ss;
		cin >> a >> ch >> b >> chh >> c;
		if (a < 3 || (a == 3 && b < 34))v.pb(pps{ INF-abs(3600*3+60*34-((a*60+b)*60+c)),ss });
