#include <iostream>

constexpr int MAX_NM = 1000;

int main(){

	int N, M;

	bool flag[MAX_NM] = {true};

	std::cin >> N >> M;

	int index = 1;
	for(int i = 0; i < N - 2; ++i){

		int count = 0;
		while(count < M){
			if(!flag[(index - 1) % N]){
				if(count == M - 1){
					flag[(index - 1) % N] = true;
				}
				++count;
			}
			++index;
		}
	}

	for(int i = 0; i < N; ++i){
		if(!flag[i]){
			std::cout << i + 1 << std::endl;
		}
	}

	return 0;
}
