map<LL, LL> zip;
LL unzip[300000];

int compress(vector<LL> &x) {
	sort(x.begin(), x.end(), [](LL x, LL y) {return x > y; });
	x.erase(unique(x.begin(), x.end()), x.end());
	for (int i = 0; i < x.size(); i++) {
		zip[x[i]] = i;
		unzip[i] = x[i];
	}
	return x.size();
}
