class BIT {
public:
	vector<LL> Bit;
	LL M;
	BIT(LL M) :
		Bit(vector<LL>(M + 1, 0)), M(M) {}
	LL sum(LL i) {
		LL s = 0;
		while (i>0) {
			s += Bit[i];
			i -= (i&(-i));
		}
		return s;
	}
	LL sum(LL i, LL j) {
		return sum(j) - sum(i - 1);
	}
	void add(LL i, LL x) {
		while (i <= M) {
			Bit[i] += x;
			i += (i&(-i));
		}
	}
};
