#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<int, int>pi;
typedef pair<ll, ll> P;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll INF = 9e18;
const double DINF = 5e14;
const double eps = 1e-10;
const int di[4] = { 1,0,-1,0 }, dj[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

string s, t;
int n;

int main() {
	cin >> s >> n;
	for (int i = 0;i < n;i++) {
		cin >> t;
		if (t == s) {
			cout << "possible" << endl;
			continue;
		}
		int now = 1;
		vector<int>v;
		for (int j = 0;j < t.size();j++) {
			if (s[0] == t[j])v.eb(j);
		}
		t += t;
		now *= 2;
		now = min(now, (int)s.size());
		while (now <= s.size()) {
			vector<int>r;
			for (auto u : v) {
				if (s.substr(0, now) == t.substr(u, now)) {
					r.eb(u);
				}
			}
			if (now == s.size()) {
				if (r.size() >= 1) {
					cout << "possible" << endl;
				}
				else {
					cout << "impossible" << endl;
				}
				break;
			}
			v = r;
			now *= 2;
			now = min(now, (int)s.size());
		}
	}
	return 0;
}
