using namespace std;
using ll = long long;
typedef unsigned long long ull;
typedef pair<ll, ll> P;
typedef pair<double, double >pd;
typedef pair<string, string> sP;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 9e14;
const double eps = 1e-10;
const double PI = 3.14159265358979323846;
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };
const int tx[8] = { -1,0,1,-1,1,-1,0,1 }, ty[8] = { -1,-1,-1,0,0,1,1,1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll n, q;
string s, r, ttf = "03:34:00";
map<string, int>mp, delay, fa;
vector<string>fast;
map<string, string>ss;

int main() {
	cin >> n;
	for (int i = 0;i < n;i++) {
		cin >> s >> r;
		if (r.substr(0, 2) == "3:" || r.substr(0, 2) == "2:" || r.substr(0, 2) == "1:" || r.substr(0, 2) == "0:") {
			r = "0" + r;
		}
		ss[s] = r;
		if (r >= ttf)mp[r]++;
		else fast.eb(r);
	}
	int cnt = 1;
	for (auto u : mp) {
		delay[u.fr] = cnt;
		cnt += u.sc;
	}
	int sz = fast.size();
	sort(ALL(fast));
	for (int i = sz - 1;i >= 0;i--) {
		int c = 1;
		if (i == 0) {
			fa[fast[i]] = cnt;
			break;
		}
		for (int j = i - 1;j >= 0;j--) {
			if (fast[j] != fast[i]) {
				fa[fast[i]] = cnt;
				cnt += c;
				i = j + 1;
				break;
			}
			if (fast[j] == fast[i] && j == 0) {
				fa[fast[i]] = cnt;
				i = 0;
				break;
			}
			c++;
		}
	}
	cin >> q;
	for (int i = 0;i < q;i++) {
		cin >> r;
		if (r.substr(0, 2) == "3:" || r.substr(0, 2) == "2:" || r.substr(0, 2) == "1:" || r.substr(0, 2) == "0:") {
			r = "0" + r;
		}
		string t = ss[r];
		if (delay[t])cout << delay[t] << endl;
		else cout << fa[t] << endl;
	}
	return 0;
}
