#include <iostream>

unsigned long long N, K;

int main(){

	std::cin >> N >> K;

	if(2 * N - 1 < K){
		std::cout << 0 << std::endl;
	}
	else if(N < K - 1){
		unsigned long long ans = (N - (K - N) + 1) / 2;
		std::cout << ans << std::endl;
	}
	else{
		std::cout << (K - 1) / 2 << std::endl;
	}

	return 0;
}
