#include <iostream>
#include <string>

int N;
std::string str;

int main(){

	std::cin >> str >> N;

	str += str;

	std::cout << str << std::endl;

	while(N--){

		std::string input;

		std::cin >> input;

		if(str.find(input) == std::string::npos){
			std::cout << "impossible" << std::endl;
		}
		else{
			std::cout << "possible" << std::endl;
		}
	}

	return 0;
}
