#include <iostream>
#include <vector>

int n,m;

std::vector<int> vec;

int main() {

    std::cin>>n>>m;

    for(int i=1;i<=n;++i)
        vec.emplace_back(i);

    int index=0;
    
    for(int i=0;i<n-1;++i){
        vec.erase(vec.begin()+index);
        index+=m-1;
        index%=vec.size();
    }

    std::cout<<vec.front()<<std::endl;

    return 0;
}
