#include <iostream>
#include <string>
#define rep(i,n) for(int i=0;i<n;++i)

using namespace std;

int n;
string s, str[202020];

int main() {
	cin >> s >> n;
	rep(i, n) cin >> str[i];
	s = s + s;
	rep(i, n) {
		if (s.find(str[i]) < s.size())
			cout << "possible" << endl;
		else
			cout << "impossible" << endl;
	}
	return 0;
}
