#include <iostream>
#include <string>

std::string ss;

int n;

int main(){

    std::cin>>ss;

    ss=ss+ss;

    std::cin>>n;

    std::string str;

    for(int i=0;i<n;++i){
        std::cin>>str;
        if(ss.find(str)!=std::string::npos)
            std::cout<<"possible"<<std::endl;
        else
            std::cout<<"impossible"<<std::endl;
    }

    return 0;
}
