using namespace std;
using ll = long long;
typedef unsigned long long ull;
typedef pair<ll, ll> P;
typedef pair<double, double >pd;
typedef pair<string, string> sP;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 9e14;
const double eps = 1e-10;
const double PI = 3.14159265358979323846;
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };
const int tx[8] = { -1,0,1,-1,1,-1,0,1 }, ty[8] = { -1,-1,-1,0,0,1,1,1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll n, q;
string s, r, ttf = "03:34:00";
map<string, int>mp, mpp;
vector<string>v;
map<string, string>ss;

int main() {
	cin >> n;
	for (int i = 0;i < n;i++) {
		cin >> s >> r;
		if (r[1] == ':') {
			r = "0" + r;
		}
		ss[s] = r;
		mp[r]++;
		v.eb(r);
	}
	sort(ALL(v));
	int cnt = 1, it = -1;
	for (int i = 0;i < v.size();i++) {
		if (v[i] >= ttf) {
			if (it == -1)it = i;
			if (!mpp[v[i]]) {
				mpp[v[i]] = cnt;
				cnt += mp[v[i]];
			}
		}
	}
	for (int i = it - 1;i >= 0;i--) {
		if (!mpp[v[i]]) {
			mpp[v[i]] = cnt;
			cnt += mp[v[i]];
		}
	}

	cin >> q;
	for (int i = 0;i < q;i++) {
		cin >> r;
		string t = ss[r];
		cout << mpp[t] << endl;
	}
	return 0;
}
