#include <iostream>
	int main() {
		int N, A, B, C, D, X, Y;
		std::cin >> N>> A >> B >> C >> D;
		X = N / A * B;Y = N / C * D;
		if (N%A == 0) {
			if (N%C == 0) {
				if (X <= Y) {
					std::cout << X<< std::endl;
				}
				else {
					std::cout << Y << std::endl;
				}
			}
		}
		else {
			if (N%C == 0) {
				std::cout << Y << std::endl;
			}
			else if (Y <= X) {
			std::cout << Y<< std::endl;
			}
			else {
				std::cout << X << std::endl;
			}
		}
		std::cout << std::endl;
		return 0;
	}
